import React, { useDebugValue, useEffect, useState } from "react";
function Chat({socket,username,room}){
    const [currentMessage,setCurrentMeassage]=useState("");
    const [messageList,setMessageList]=useState([]);
    const sendMessage=async ()=>{
        if(currentMessage!==""){
            const messageData={
                room:room,
                author:username,
                message:currentMessage,
                time:new Date(Date.now()).getHours()+":"+new Date(Date.now()).getMinutes(),

            };
            await socket.emit("send_message",messageData);
        }
    }

    useEffect(()=>{socket.on("receive_message",(data)=>{console.log(data)},[socket]);
    })

    return (
        <div className="chat-window">
            <div className="chat-header"><p>Live Chat</p></div>
            <div className="chat-body">
                <input type="text" placeholder="start typing..." onChange={(event)=>{setCurrentMeassage(event.target.value)}}/>
            </div><br/>
            <div className="chat-footer">
                <button onClick={sendMessage}>&#9658;</button>
            </div>
        </div>
    )
}
export default Chat;
